import React, { useState, useEffect } from "react";
import Card from "./Card";
import Pokeinfo from "./Pokeinfo";
import axios from "axios";

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState("https://pokeapi.co/api/v2/pokemon/");
  const [nextUrl, setNextUrl] = useState();
  const [prevUrl, setPrevUrl] = useState();
  const [pokeDex, setPokeDex] = useState();
  const [searchTerm, setSearchTerm] = useState("");
  const [autocompleteList, setAutocompleteList] = useState([]);

  const pokeFun = async () => {
    setLoading(true);
    try {
      const res = await axios.get(url);
      setNextUrl(res.data.next);
      setPrevUrl(res.data.previous);
      getPokemon(res.data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching Pokemon list:", error);
      setLoading(false);
    }
  };

  const getPokemon = async (res) => {
    res.forEach(async (item) => {
      try {
        const result = await axios.get(item.url);
        setPokeData((state) => {
          state = [...state, result.data];
          state.sort((a, b) => (a.id > b.id ? 1 : -1));
          return state;
        });
      } catch (error) {
        console.error("Error fetching Pokemon data:", error);
      }
    });
  };

  const handleSearch = async () => {
    if (searchTerm.trim() !== "") {
      try {
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon/${searchTerm.toLowerCase()}`
        );
        setPokeData([response.data]);
        setAutocompleteList([]); // Clear autocomplete list when a specific Pokemon is found
      } catch (error) {
        console.error("Error fetching Pokemon data:", error);
      }
    }
  };

  const handleAutocomplete = async () => {
    if (searchTerm.trim() !== "") {
      try {
        const response = await axios.get(
          `https://pokeapi.co/api/v2/pokemon?limit=10&offset=0`
        );
        const pokemonList = response.data.results.map((pokemon) => pokemon.name);
        const filteredList = pokemonList.filter((name) =>
          name.startsWith(searchTerm.toLowerCase())
        );
        setAutocompleteList(filteredList);
      } catch (error) {
        console.error("Error fetching Pokemon list for autocomplete:", error);
      }
    } else {
      setAutocompleteList([]); // Clear autocomplete list when input is empty
    }
  };

  useEffect(() => {
    pokeFun();
  }, [url]);

  return (
    <>
      <div className="container">
        <div className="left-content">
          <div className="search-container">
            <input
              type="text"
              placeholder="Enter Pokemon name or ID"
              value={searchTerm}
              onChange={(e) => {
                setSearchTerm(e.target.value);
                handleAutocomplete();
              }}
            />
            <button onClick={() => handleSearch()}>Search</button>
            {autocompleteList.length > 0 && (
              <ul className="autocomplete-list">
                {autocompleteList.map((pokemonName) => (
                  <li
                    key={pokemonName}
                    onClick={() => {
                      setSearchTerm(pokemonName);
                      handleSearch();
                    }}
                  >
                    {pokemonName}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <Card
            pokemon={pokeData}
            loading={loading}
            infoPokemon={(poke) => setPokeDex(poke)}
          />

          <div className="btn-group">
            {prevUrl && (
              <button
                onClick={() => {
                  setPokeData([]);
                  setUrl(prevUrl);
                }}
              >
                Previous
              </button>
            )}

            {nextUrl && (
              <button
                onClick={() => {
                  setPokeData([]);
                  setUrl(nextUrl);
                }}
              >
                Next
              </button>
            )}
          </div>
        </div>
        <div className="right-content">
          <Pokeinfo data={pokeDex} />
        </div>
      </div>
    </>
  );
};

export default Main;
